import React, { useEffect, useState } from "react";

const rewardAmountMap = {
  a: "20,000원 상당",
  b: "100,000원 상당",
  c: "100,000원 상당",
  d: "200,000원 상당",
  e: "400,000원 상당",
  f: "금액 미상"
};

const rewardTextMap = {
  a: "가. 담배꽁초, 휴지 등 휴대하고 있는 폐기물을 버린 경우",
  b: "나. 폐기물을 비닐봉지 등에 담아 아무 곳에나 버리는 행위",
  c: "다. 공원, 유원지 등에서 발생한 폐기물을 수거하지 아니하는 행위",
  d: "라. 차량, 손수레 등 운반장비로 폐기물 버리는 행위",
  e: "마. 사업활동 과정에서 발생 폐기물",
  f: "바. 기타 금액 미상"
};

export default function TrashReportBoard() {
  const [reports, setReports] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(null);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [rewardType, setRewardType] = useState("");
  const [image, setImage] = useState(null);
  const [sortBy, setSortBy] = useState("created_at");
  const [sortOrder, setSortOrder] = useState("desc");
  const [popupReport, setPopupReport] = useState(null);

  useEffect(() => {
    document.title = '분리배출 신고 게시판';
    fetchReports();
  }, [sortBy, sortOrder]);

  const fetchReportDetail = async (report_id) => {
    const res = await fetch(`/api/reports/${report_id}`);
    if (!res.ok) return alert("데이터 불러오기 실패");
    const data = await res.json();
    setPopupReport(data);
  };

  const fetchReports = async () => {
    const res = await fetch(`/api/reports?sortBy=${sortBy}&order=${sortOrder}`);
    const list = await res.json();
    setReports(list);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    formData.append("reward", rewardType);
    if (image) formData.append("image", image);
    await fetch("/api/reports", {
      method: "POST",
      body: formData
    });
    resetForm();
    fetchReports();
  };

  const handleDelete = async (id) => {
    await fetch(`/api/reports/${id}`, { method: "DELETE" });
    fetchReports();
  };

  const handleEdit = async (e, id) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    if (image) formData.append("image", image);
    await fetch(`/api/reports/${id}`, {
      method: "PUT",
      body: formData
    });
    resetForm();
    setShowEditForm(null);
    fetchReports();
  };

  const handleLike = async (id) => {
    await fetch(`/api/reports/${id}/like`, { method: "POST" });
    fetchReports();
  };

  const resetForm = () => {
    setTitle("");
    setContent("");
    setRewardType("");
    setImage(null);
    setShowForm(false);
  };

  return (
    <div className="max-w-3xl mx-auto p-6 font-sans text-gray-800">
      <h1 className="text-3xl font-bold text-center mb-6">🧾 분리배출 신고 게시판</h1>

      <div className="flex justify-between items-center mb-4">
        <button onClick={() => setShowForm(true)} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">+ 민원 제보</button>
        <div className="flex gap-2">
          <select value={sortBy} onChange={e => setSortBy(e.target.value)} className="border rounded px-2 py-1">
            <option value="created_at">시간순</option>
            <option value="likes">추천순</option>
          </select>
          <select value={sortOrder} onChange={e => setSortOrder(e.target.value)} className="border rounded px-2 py-1">
            <option value="desc">내림차순</option>
            <option value="asc">오름차순</option>
          </select>
        </div>
      </div>

      {showForm && (
        <form onSubmit={handleSubmit} className="bg-gray-100 rounded-lg p-6 mb-6 shadow-md">
          <h2 className="text-xl font-semibold mb-4">📝 민원 작성</h2>
          <input type="text" placeholder="제목 입력" value={title} onChange={(e) => setTitle(e.target.value)} required className="w-full border p-2 mb-3 rounded" />
          <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} className="mb-3" />
          <textarea value={content} onChange={(e) => setContent(e.target.value)} placeholder="민원 내용" required className="w-full border p-2 mb-3 rounded h-24" />
          <select value={rewardType} onChange={(e) => setRewardType(e.target.value)} required className="w-full border p-2 mb-3 rounded">
            <option value="" disabled>포상금 유형 선택</option>
            {Object.entries(rewardTextMap).map(([key, label]) => (
              <option value={key} key={key}>{label} ({rewardAmountMap[key]})</option>
            ))}
          </select>
          <div className="flex gap-3">
            <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">제보</button>
            <button type="button" onClick={() => setShowForm(false)} className="bg-gray-400 text-white px-4 py-2 rounded hover:bg-gray-500">취소</button>
          </div>
        </form>
      )}

      <h2 className="text-2xl font-semibold mb-4">📋 전체 민원 목록</h2>
      {reports.map((r) => (
        <div key={r.report_id} className="border rounded-lg p-4 mb-4 shadow-sm">
          <div className="text-lg font-bold mb-1">{r.title}</div>
          <div className="mb-2">{r.content}</div>
          {r.image_url && <img src={r.image_url} width="200" alt="report" className="mb-2 rounded-md" />}
          <div className="text-sm text-gray-600 mb-1">포상금: {rewardAmountMap[r.reward] || '-'}</div>
          <div className="text-xs text-gray-500 mb-1">{new Date(r.created_at).toLocaleString()}</div>
          <div className="text-sm font-medium mb-2">👍 추천수: {r.likes || 0}</div>
          <div className="flex gap-2">
            <button onClick={() => handleLike(r.report_id)} className="text-green-700 hover:underline">추천</button>
            <button onClick={() => setPopupReport(r)} className="text-orange-600 hover:underline">신고안내</button>
            <button onClick={() => handleDelete(r.report_id)} className="text-red-600 hover:underline">삭제</button>
            <button onClick={() => {
              setShowEditForm(r.report_id);
              setTitle(r.title);
              setContent(r.content);
              setImage(null);
            }} className="text-blue-600 hover:underline">수정</button>
          </div>

          {showEditForm === r.report_id && (
            <form onSubmit={(e) => handleEdit(e, r.report_id)} className="mt-4 bg-gray-50 p-4 rounded">
              <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="제목 수정" required className="w-full border p-2 mb-2 rounded" />
              <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} className="mb-2" />
              <textarea value={content} onChange={(e) => setContent(e.target.value)} required className="w-full border p-2 mb-2 rounded h-20" />
              <div className="flex gap-3">
                <button type="submit" className="bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-700">저장</button>
                <button type="button" onClick={() => setShowEditForm(null)} className="bg-gray-400 text-white px-4 py-1 rounded hover:bg-gray-500">취소</button>
              </div>
            </form>
          )}
        </div>
      ))}

      {popupReport && (
        <div className="fixed top-1/4 left-1/2 transform -translate-x-1/2 -translate-y-1/4 bg-white border shadow-lg rounded-lg p-6 z-50 w-80">
          <button onClick={() => setPopupReport(null)} className="float-right text-red-600 font-bold">X</button>
          <h3 className="text-lg font-semibold mb-2">📢 신고 안내</h3>
          <p className="mb-1">{rewardTextMap[popupReport.reward] || "선택 안됨"}</p>
          <p className="mb-2 font-medium">💰 포상금: {rewardAmountMap[popupReport.reward]}</p>
          <a href="https://www.sejong.go.kr/citizen/sub03_0307.do" target="_blank" rel="noreferrer" className="text-blue-700 underline">세종시 안전신문고 신고 바로가기</a>
        </div>
      )}
    </div>
  );
}
