import React, { useState } from "react";
import "./trashsort_ai.css";

const WasteSorting = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    const reader = new FileReader();
    reader.onload = () => {
      setPreviewUrl(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleAnalyze = async () => {
    if (!selectedFile) {
      alert("이미지를 선택해주세요.");
      return;
    }

    setLoading(true);
    setResult(null);

    const formData = new FormData();
    formData.append("image", selectedFile);

    try {
      const response = await fetch("/analyze/upload-analyze", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setResult(data);
    } catch (err) {
      setResult({ error: "분석 실패" });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>쓰레기 분류 시스템 테스트 페이지</h1>

      <div className="upload-section">
        <h3>이미지를 업로드하여 쓰레기를 분류해보세요</h3>
        <p>지원 형식: JPG, PNG, GIF</p>
        <input
          type="file"
          id="imageInput"
          accept="image/*"
          onChange={handleFileChange}
        />
        <label htmlFor="imageInput" className="upload-btn">
          이미지 선택
        </label>
      </div>

      {previewUrl && (
        <div className="preview">
          <h3>미리보기</h3>
          <img src={previewUrl} alt="미리보기" />
          <br />
          <br />
          <button className="upload-btn" onClick={handleAnalyze}>
            분석하기
          </button>
        </div>
      )}

      <div id="result">
        {loading && <div className="loading">이미지를 분석하고 있습니다...</div>}
        {result && (
          <pre>{JSON.stringify(result, null, 2)}</pre>
        )}
      </div>
    </div>
  );
};

export default WasteSorting;
