import React, { useEffect, useState } from "react";

const rewardAmountMap = {
  a: "20,000Ïõê ÏÉÅÎãπ",
  b: "100,000Ïõê ÏÉÅÎãπ",
  c: "100,000Ïõê ÏÉÅÎãπ",
  d: "200,000Ïõê ÏÉÅÎãπ",
  e: "400,000Ïõê ÏÉÅÎãπ",
  f: "Í∏àÏï° ÎØ∏ÏÉÅ"
};

const rewardTextMap = {
  a: "Í∞Ä. Îã¥Î∞∞ÍΩÅÏ¥à, Ìú¥ÏßÄ Îì± Ìú¥ÎåÄÌïòÍ≥† ÏûàÎäî ÌèêÍ∏∞Î¨ºÏùÑ Î≤ÑÎ¶∞ Í≤ΩÏö∞",
  b: "ÎÇò. ÌèêÍ∏∞Î¨ºÏùÑ ÎπÑÎãêÎ¥âÏßÄ Îì±Ïóê Îã¥ÏïÑ ÏïÑÎ¨¥ Í≥≥ÏóêÎÇò Î≤ÑÎ¶¨Îäî ÌñâÏúÑ",
  c: "Îã§. Í≥µÏõê, Ïú†ÏõêÏßÄ Îì±ÏóêÏÑú Î∞úÏÉùÌïú ÌèêÍ∏∞Î¨ºÏùÑ ÏàòÍ±∞ÌïòÏßÄ ÏïÑÎãàÌïòÎäî ÌñâÏúÑ",
  d: "Îùº. Ï∞®Îüâ, ÏÜêÏàòÎ†à Îì± Ïö¥Î∞òÏû•ÎπÑÎ°ú ÌèêÍ∏∞Î¨º Î≤ÑÎ¶¨Îäî ÌñâÏúÑ",
  e: "Îßà. ÏÇ¨ÏóÖÌôúÎèô Í≥ºÏ†ïÏóêÏÑú Î∞úÏÉù ÌèêÍ∏∞Î¨º",
  f: "Î∞î. Í∏∞ÌÉÄ Í∏àÏï° ÎØ∏ÏÉÅ"
};

export default function TrashReportBoard() {
  const [reports, setReports] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(null);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [rewardType, setRewardType] = useState("");
  const [image, setImage] = useState(null);
  const [sortBy, setSortBy] = useState("created_at");
  const [sortOrder, setSortOrder] = useState("desc");
  const [popupReport, setPopupReport] = useState(null);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    document.title = "Î∂ÑÎ¶¨Î∞∞Ï∂ú Ïã†Í≥† Í≤åÏãúÌåê";
    fetchReports();
  }, [sortBy, sortOrder, page]);

  const fetchReports = async () => {
    const sortParam = sortBy === "created_at" ? "date" : sortBy;
    const res = await fetch(`/api/reports?sort=${sortParam}&order=${sortOrder}&page=${page}&limit=5`);
    const result = await res.json();
    setReports(result.data);
    setTotalPages(Math.ceil(result.total / result.limit));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    formData.append("reward", rewardType);
    if (image) formData.append("image", image);
    await fetch("/api/reports", {
      method: "POST",
      body: formData,
    });
    setShowForm(false);
    resetForm();
    fetchReports();
  };

  const handleDelete = async (id) => {
    await fetch(`/api/reports/${id}`, { method: "DELETE" });
    fetchReports();
  };

  const handleEdit = async (e, id) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    if (image) formData.append("image", image);
    await fetch(`/api/reports/${id}`, {
      method: "PUT",
      body: formData,
    });
    setShowEditForm(null);
    resetForm();
    fetchReports();
  };

  const handleLike = async (id) => {
    await fetch(`/api/reports/${id}/like`, { method: "POST" });
    fetchReports();
  };

  const resetForm = () => {
    setTitle("");
    setContent("");
    setRewardType("");
    setImage(null);
  };

  return (
    <div style={{ maxWidth: 600, margin: "40px auto", fontFamily: "sans-serif" }}>
      <h1>Î∂ÑÎ¶¨Î∞∞Ï∂ú Ïã†Í≥† Í≤åÏãúÌåê</h1>
      <button onClick={() => setShowForm(true)}>+ ÎØºÏõê Ï†úÎ≥¥</button>

      <div style={{ marginTop: 16 }}>
        <label>Ï†ïÎ†¨ Í∏∞Ï§Ä: </label>
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="created_at">ÏãúÍ∞ÑÏàú</option>
          <option value="likes">Ï∂îÏ≤úÏàú</option>
        </select>
        <select value={sortOrder} onChange={(e) => setSortOrder(e.target.value)}>
          <option value="desc">ÎÇ¥Î¶ºÏ∞®Ïàú</option>
          <option value="asc">Ïò§Î¶ÑÏ∞®Ïàú</option>
        </select>
      </div>

      {showForm && (
        <form onSubmit={handleSubmit} style={formStyle}>
          <h2>ÎØºÏõê ÏûëÏÑ±</h2>
          <input type="text" placeholder="Ï†úÎ™© ÏûÖÎ†•" value={title} onChange={(e) => setTitle(e.target.value)} required /><br />
          <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} /><br />
          <textarea value={content} onChange={(e) => setContent(e.target.value)} placeholder="ÎØºÏõê ÎÇ¥Ïö©" required /><br />
          <select value={rewardType} onChange={(e) => setRewardType(e.target.value)} required>
            <option value="" disabled>Ìè¨ÏÉÅÍ∏à Ïú†Ìòï ÏÑ†ÌÉù</option>
            {Object.entries(rewardTextMap).map(([key, label]) => (
              <option value={key} key={key}>{label} ({rewardAmountMap[key]})</option>
            ))}
          </select><br />
          <button type="submit">Ï†úÎ≥¥</button>
          <button type="button" onClick={() => setShowForm(false)}>Ï∑®ÏÜå</button>
        </form>
      )}

      <h2>Ï†ÑÏ≤¥ ÎØºÏõê Î™©Î°ù</h2>
      {reports.map((r) => (
        <div key={r._id || r.report_id} style={reportStyle}>
          <div><b>{r.title}</b></div>
          <div>{r.content}</div>
          {r.image_url && <img src={r.image_url} width="100" alt="report" style={{ margin: "8px 0" }} />}<br />
          <div><b>Ìè¨ÏÉÅÍ∏à ÏßÄÍ∏âÏï°:</b> {rewardAmountMap[r.reward] || '-'}</div>
          <div style={{ color: '#888', fontSize: '0.9em' }}>{new Date(r.created_at).toLocaleString()}</div>
          <div><b>Ï∂îÏ≤úÏàò:</b> {r.likes || 0}</div>
          <button onClick={() => handleLike(r._id || r.report_id)} style={{ color: 'green' }}>üëç Ï∂îÏ≤ú</button>
          <button onClick={() => setPopupReport(r)} style={{ marginLeft: 8 }}>üì¢ Ïã†Í≥†</button>
          <button onClick={() => handleDelete(r._id || r.report_id)} style={{ color: 'red', marginLeft: 8 }}>ÏÇ≠Ï†ú</button>
          <button onClick={() => {
            setShowEditForm(r._id || r.report_id);
            setTitle(r.title);
            setContent(r.content);
            setImage(null);
          }} style={{ color: '#0a67d6', marginLeft: 8 }}>ÏàòÏ†ï</button>

          {showEditForm === (r._id || r.report_id) && (
            <form onSubmit={(e) => handleEdit(e, r._id || r.report_id)} style={formStyle}>
              <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Ï†úÎ™© ÏàòÏ†ï" required /><br />
              <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} /><br />
              <textarea value={content} onChange={(e) => setContent(e.target.value)} required /><br />
              <button type="submit">Ï†ÄÏû•</button>
              <button type="button" onClick={() => setShowEditForm(null)}>Ï∑®ÏÜå</button>
            </form>
          )}
        </div>
      ))}

      {/* ÌéòÏù¥ÏßÄÎÑ§Ïù¥ÏÖò UI */}
      <div style={{ marginTop: 24, textAlign: 'center' }}>
        <button disabled={page === 1} onClick={() => setPage(page - 1)}>Ïù¥Ï†Ñ</button>
        <span style={{ margin: "0 12px" }}>{page} / {totalPages}</span>
        <button disabled={page === totalPages} onClick={() => setPage(page + 1)}>Îã§Ïùå</button>
      </div>

      {popupReport && (
        <div style={popupStyle}>
          <button onClick={() => setPopupReport(null)} style={{ float: 'right' }}>X</button>
          <h3>üì¢ Ïã†Í≥† ÏïàÎÇ¥</h3>
          <p>{rewardTextMap[popupReport.reward] || "ÏÑ†ÌÉù ÏïàÎê®"}</p>
          <p><b>Ìè¨ÏÉÅÍ∏à:</b> {rewardAmountMap[popupReport.reward]}</p>
          <p><a href="https://www.sejong.go.kr/citizen/sub03_0307.do" target="_blank" rel="noreferrer">ÏÑ∏Ï¢ÖÏãú ÏïàÏ†ÑÏã†Î¨∏Í≥† Ïã†Í≥† Î∞îÎ°úÍ∞ÄÍ∏∞</a></p>
        </div>
      )}
    </div>
  );
}

const formStyle = {
  background: '#f9f9f9',
  borderRadius: 8,
  padding: 16,
  margin: '18px 0'
};

const reportStyle = {
  border: '1px solid #ddd',
  marginBottom: 12,
  padding: 12,
  borderRadius: 8
};

const popupStyle = {
  position: 'fixed',
  top: '20%',
  left: '50%',
  transform: 'translate(-50%, -20%)',
  backgroundColor: '#fff',
  border: '1px solid #ccc',
  padding: 20,
  zIndex: 1000,
  width: 300,
  boxShadow: '0px 4px 12px rgba(0, 0, 0, 0.2)'
};
