// report.jsx
import React, { useState, useEffect } from "react";
import "./Report.css";
import { FaTrashAlt, FaEdit, FaThumbsUp } from "react-icons/fa";
import { MdLocationOn } from "react-icons/md";
import { IoIosImages } from "react-icons/io";

const rewardData = [
  { type: "담배꽁초", reward: 5000, icon: "🚭" },
  { type: "대형폐기물", reward: 20000, icon: "🧳" },
  { type: "쓰레기무단투기", reward: 10000, icon: "🗑️" },
  { type: "기타", reward: 3000, icon: "❗" },
];

export default function Report() {
  const [selectedType, setSelectedType] = useState(null);
  const [image, setImage] = useState(null);
  const [address, setAddress] = useState("");
  const [description, setDescription] = useState("");
  const [reports, setReports] = useState([]);

  const handleImageUpload = (e) => {
    setImage(e.target.files[0]);
  };

  const submitReport = () => {
    if (!selectedType || !image) return;
    const newReport = {
      id: Date.now(),
      type: selectedType,
      content: description,
      image,
      date: new Date().toLocaleString(),
    };
    setReports([newReport, ...reports]);
    setSelectedType(null);
    setImage(null);
    setAddress("");
    setDescription("");
  };

  return (
    <div className="report-container">
      <h2 className="report-title">민원 제보</h2>
      <p className="report-sub">환경 오염을 신고하고 포상금을 받아보세요</p>

      <div className="report-grid">
        <div className="report-form">
          <h3>새 민원 작성</h3>
          <div className="type-select">
            {rewardData.map((item) => (
              <div
                key={item.type}
                className={`type-box ${selectedType === item.type ? "selected" : ""}`}
                onClick={() => setSelectedType(item.type)}
              >
                <div className="type-icon">{item.icon}</div>
                <div>{item.type}</div>
                <div className="reward">포상금: {item.reward.toLocaleString()}원</div>
              </div>
            ))}
          </div>

          <div className="photo-upload">
            <IoIosImages className="upload-icon" />
            <label htmlFor="upload-btn">사진 선택</label>
            <input id="upload-btn" type="file" accept="image/*" onChange={handleImageUpload} hidden />
          </div>

          <input
            type="text"
            placeholder="주소를 입력하거나 현재 위치를 사용하세요"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            className="address-input"
          />

          <textarea
            placeholder="신고 내용을 자세히 설명해주세요"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="desc-input"
          />

          <div className="submit-area">
            <button className="submit-btn" onClick={submitReport}>민원 제출</button>
            <button className="cancel-btn">취소</button>
          </div>
        </div>

        <div className="report-list">
          <h3>내 민원 목록</h3>
          {reports.map((r) => (
            <div className="report-card" key={r.id}>
              <div className="report-top">
                <div>{rewardData.find((d) => d.type === r.type)?.icon} <strong>{r.type}</strong></div>
                <span className="status">처리중</span>
              </div>
              <div>{r.content}</div>
              <div className="report-bottom">
                <span>{r.date}</span>
                <div className="report-actions">
                  <FaThumbsUp /> <span>추천 0</span>
                  <FaEdit className="edit-icon" />
                  <FaTrashAlt className="delete-icon" />
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
